#merge arr and arr1 by looking at the second element in each array in arr and matching it to the first element in each array in arr1 and putting the result in array result
def merge(arr,arr1):
    result = []
    for i in arr:
        for j in arr1:
            if i[-1] == j[0]:
                res = [num for num in i]
                res.append(j[1])
                result.append(res)
    result = np.array(result)
    return result

def merge_list(list_of_pairs):
    matches = list_of_pairs[0]
    for i in range(len(list_of_pairs)-1):
        matches = merge(matches,list_of_pairs[i+1])
    matches = np.array(matches)
    return np.squeeze(matches)

